# Diferenciación serie original (si num_diff_raw > 0)
if (num_diff_raw > 0) {
  precio_diff_raw <- diff(precio_mens_ts, differences = num_diff_raw)
} else {
  precio_diff_raw <- precio_mens_ts
}

# Transformación logarítmica y diferenciación (si num_diff_log > 0)
precio_log_ts <- log(precio_mens_ts)
if (num_diff_log > 0) {
  precio_diff_log <- diff(precio_log_ts, differences = num_diff_log)
} else {
  precio_diff_log <- precio_log_ts
}

# Graficar para comparar estabilidad de varianza y tendencia eliminada
autoplot(precio_diff_raw) +
  labs(title = "Serie Mensual Diferenciada (original)",
       y = "Δ Precio", x = "Tiempo") +
  theme_minimal()
autoplot(precio_diff_log) +
  labs(title = "Serie Mensual Log-Diferenciada",
       y = "Δ log(Precio)", x = "Tiempo") +
  theme_minimal()
